  */

  TYPE t_r_operacion IS RECORD(
    tip_accion VARCHAR2(1),
    des_accion VARCHAR2(20));

  TYPE t_t_operacion IS TABLE OF t_r_operacion INDEX BY BINARY_INTEGER;

  /* Operaciones:
